  reset() {
    for (var i = 0; i < this.card.length; i++) {
      this.card[i].status = false;
    }
    this.shuffle(this.card);
    this.count = 0

  }
  openDialog(): void {
    const dialogRef = this.dialog.open(ModalComponent, {
      data: this.result,
    });

    dialogRef.afterClosed().subscribe(result => {
      console.log(result)
      if (result) {
        this.reset()
      }

    });
  }

